currentVersionById.map:
function(doc) {
  if (doc.context === 'journal') {
    var lastVersionId = doc.versions.slice(-1)[0]
    emit([doc.type, doc._id], {'_id': lastVersionId})
  }
}

byAttribute.map:
function(doc) {
  if (doc.context === 'journal' && doc.type === 'place' && doc.data && doc.data.properties) {
    var lastVersionId = doc.versions.slice(-1)[0]
    const attributes = doc.data.properties
     for(key in attributes) {
      const value = attributes[key]
      emit([doc.type, key, value], {'_id': lastVersionId})
    }
  }
}
